@@ -28,7 +21,7 @@
       pstr_loc = Location.none;
       pstr_desc = Pstr_eval ({
           pexp_loc = Location.none;
-          pexp_desc = Pexp_constant (CONST_STRING (msg, None));
+          pexp_desc = Pexp_constant (Parsetree.Pconst_string (msg, None));
           pexp_attributes = [];
         }, []);
     }]
@@ -112,7 +105,7 @@
