         pstr_desc = Pstr_eval ({
-            pexp_desc = Pexp_constant (CONST_STRING (msg, _));
+            pexp_desc = Pexp_constant (Parsetree.Pconst_string (msg, _));
           }, _);
       }] -> msg
     | _ -> "Warning: extension produced an incorrect syntax-error node"

